import{_ as e,c as a,o as r,a5 as t}from"./chunks/framework.C61Lbh9p.js";const f=JSON.parse('{"title":"laravel-admin","description":"","frontmatter":{},"headers":[],"relativePath":"zh-CN/guide/index.md","filePath":"zh-CN/guide/index.md"}'),l={name:"zh-CN/guide/index.md"},i=t('<h1 id="laravel-admin" tabindex="-1">laravel-admin <a class="header-anchor" href="#laravel-admin" aria-label="Permalink to &quot;laravel-admin&quot;">​</a></h1><p><code>laravel-admin</code> 是一个可以快速帮你构建后台管理的工具，它提供的页面组件和表单元素等功能，能帮助你使用很少的代码就实现功能完善的后台管理功能。</p><div class="tip custom-block"><p class="custom-block-title">注意</p><p>当前版本(v1.0.*)需要安装<code>PHP 8.1+</code>和<code>Laravel 11</code></p></div><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性" aria-label="Permalink to &quot;特性&quot;">​</a></h2><ul><li>内置用户和权限系统</li><li><code>model-table</code>支持快速构建数据表格</li><li><code>model-form</code>支持快速构建数据表单</li><li><code>model-tree</code>支持快速构建树状数据</li><li>内置40+种form元素组件、以及支持扩展组件</li><li>支持<code>Laravel</code>的多种模型关系</li><li><code>mysql</code>、<code>mongodb</code>、<code>pgsql</code>等多数据库支持</li><li>支持引入第三方前端库</li><li>数据库和artisan命令行工具的web实现</li><li>支持自定义图表</li><li>多种常用web组件</li><li>支持本地和oss文件上传</li></ul><h2 id="依赖" tabindex="-1">依赖 <a class="header-anchor" href="#依赖" aria-label="Permalink to &quot;依赖&quot;">​</a></h2><p><code>laravel-admin</code> 基于以下组件或者服务:</p><ul><li><a href="https://laravel.com/" target="_blank" rel="noreferrer">Laravel</a></li><li><a href="https://almsaeedstudio.com/" target="_blank" rel="noreferrer">AdminLTE</a></li><li><a href="http://eonasdan.github.io/bootstrap-datetimepicker/" target="_blank" rel="noreferrer">Datetimepicker</a></li><li><a href="http://fontawesome.io/" target="_blank" rel="noreferrer">font-awesome</a></li><li><a href="http://momentjs.com/" target="_blank" rel="noreferrer">moment</a></li><li><a href="https://www.google.com/maps" target="_blank" rel="noreferrer">Google map</a></li><li><a href="http://lbs.qq.com/" target="_blank" rel="noreferrer">Tencent map</a></li><li><a href="https://github.com/kartik-v/bootstrap-fileinput" target="_blank" rel="noreferrer">bootstrap-fileinput</a></li><li><a href="https://github.com/defunkt/jquery-pjax" target="_blank" rel="noreferrer">jquery-pjax</a></li><li><a href="http://dbushell.github.io/Nestable/" target="_blank" rel="noreferrer">Nestable</a></li><li><a href="http://codeseven.github.io/toastr/" target="_blank" rel="noreferrer">toastr</a></li><li><a href="http://github.com/vitalets/x-editable" target="_blank" rel="noreferrer">X-editable</a></li><li><a href="https://github.com/wpic/bootstrap-number-input" target="_blank" rel="noreferrer">bootstrap-number-input</a></li><li><a href="https://github.com/itsjavi/fontawesome-iconpicker" target="_blank" rel="noreferrer">fontawesome-iconpicker</a></li></ul><h2 id="交流" tabindex="-1">交流 <a class="header-anchor" href="#交流" aria-label="Permalink to &quot;交流&quot;">​</a></h2><p>QQ群:167834740</p><h2 id="license" tabindex="-1">License <a class="header-anchor" href="#license" aria-label="Permalink to &quot;License&quot;">​</a></h2><p><code>laravel-admin</code> is licensed under <a href="https://github.com/explore-pu/laravel-admin/blob/main/LICENSE" target="_blank" rel="noreferrer">The MIT License (MIT)</a>.</p>',12),o=[i];function n(d,c,s,h,m,p){return r(),a("div",null,o)}const u=e(l,[["render",n]]);export{f as __pageData,u as default};
